  latitude: number
  longitude: number
}

interface OfficeModel {
  name: string
  slug: string
  location: LocationModel
}

function App() {

  const offices: OfficeModel[] = [
    {
      name: 'Singapore',
      slug: 'singapore',
      location: {
        latitude: 1.285194,
        longitude: 103.8522982
      }
    },
    {
      name: 'London',
      slug: 'london',
      location: {
        latitude: 51.5049375,
        longitude: -0.0964509
      }
