import React, { useState } from 'react';
import './App.css';
import GoogleMapReact from 'google-map-react';
import Marker, { MarkerStyle } from './components/Marker';
import { OfficeModel } from './models/OfficeModel';
import { offices } from './data/offices'
import { useQuery, useQueryClient } from 'react-query';
import { CoordinateModel } from './models/CoordinateModel';

const getDrivers = (): DriverModel[] => [
  {
    "id": "0-xmxr1d2uvp9",
    "coordinate": {
      "lat": -0.00012070285158789822,
      "lng": 0.0032262641730860216,
    },
    "bearing": 55
  },
  {
    "id": "1-6xo6j9agy6",
    "coordinate": {
      "lat": -0.001490121361325301,
      "lng": -0.004803436455318129,
    },
    "bearing": 86
  },
  {
    "id": "2-nlspzop4gl",
    "coordinate": {
      "lat": -0.005499108513977029,
      "lng": -0.0014426628774745823,
    },
    "bearing": 9
  },
  {
    "id": "3-9zg4t1loghr",
    "coordinate": {
      "lat": 0.002036699403583106,
      "lng": -0.004170542055086246,
    },
    "bearing": 200
  },
]

interface DriverModel {
  id: string,
  // Coordinate driver on map in {latitude, longitude}
  coordinate: CoordinateModel,
  // Driver facing angle in clockwise. North: 0 degree, easet: 90 degree, south: 180 deg, west: 270 deg
  bearing: number
}

function App() {

  const getCurrentOffice = (slug: string): OfficeModel => offices
    .find((ofc) => ofc.slug === slug) || offices[0];

  const driverQuery = useQuery('drivers', getDrivers, {
    initialData: [],
  });

  const [officeData, setOfficeData] = useState<OfficeModel>(offices[0]);
  const zoom = 11;

  return (
    <div className="App">
      <div>
        Choose location:
        <select
          onChange={(e) => setOfficeData({...officeData, ...getCurrentOffice(e.target.value)})}
          >
          {
            offices
              .map((office) => (
                <option 
                key={office.slug} 
                value={office.slug}
                >{office.name}</option>
              ))
          }
        </select>
      </div>

      <div>Slider car number</div>

      {driverQuery.data?.map((driver) => (
        <h3>{driver.id}</h3>
      ))}

      <div style={{width: '100%', height: '320px'}}>
        <GoogleMapReact
           bootstrapURLKeys={{ key: 'AIzaSyCzOjhJjfh6FGaYAxpvCekVI8Zvn2JWyZE'}}
           defaultCenter={officeData.location}
           defaultZoom={zoom}
           yesIWantToUseGoogleMapApiInternals
           center={officeData.location}
          >
            <Marker
                key={officeData.slug}
                text={officeData.name}
                lat={officeData.location.lat}
                lng={officeData.location.lng}
                markerStyle={MarkerStyle.office}
              />

      {driverQuery.data?.map((driver) => (

        <Marker
        key={officeData.slug}
        text={officeData.name}
        lat={officeData.location.lat}
        lng={officeData.location.lng}
        markerStyle={MarkerStyle.car}
      />
      ))}
        </GoogleMapReact>
      </div>

      <div>
        Refresh rate interval: 
        {' '}
        <input type="number"/>
      </div>

    </div>
  );
}

export default App;
